INSERT INTO `biz_product` VALUES (3, '家庭套票', 1000.00, '与家人一起享受游泳的乐趣！购买我们的家庭套票，全家人畅游游泳池，共度美好时光。我们设有专门的家庭泳池区域，提供安全舒适的环境，让您和家人尽情畅游，放松身心。', 0, 30, 'http://localhost:8080/upload/imgs/20240331\\1711897461558_821.jpg', '2024-04-03 14:39:43');
